import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import ApiService from '../../service/ApiService'; // Assuming your service is in a file called ApiService.js
import DatePicker from 'react-datepicker';
// import 'react-datepicker/dist/react-datepicker.css';



const RoomDetailsPage = ({ roomDetails }) => {
  const [checkInDate, setCheckInDate] = useState(null);
  const [checkOutDate, setCheckOutDate] = useState(null);
  const [numAdults, setNumAdults] = useState(1);
  const [numChildren, setNumChildren] = useState(0);
  const [totalPrice, setTotalPrice] = useState(0);
  const [totalGuests, setTotalGuests] = useState(0);
  const [errorMessage, setErrorMessage] = useState("");

  // Varsayılan değer kontrolü
  const defaultRoomDetails = {
    roomName: "Standart Oda",
    roomDescription: "Bu oda hakkında bilgi mevcut değil.",
    roomPrice: 0,
  };

  const room = roomDetails || defaultRoomDetails;

  const handleConfirmBooking = () => {
    // Giriş ve çıkış tarihleri kontrolü
    if (!checkInDate || !checkOutDate) {
      setErrorMessage("Lütfen giriş ve çıkış tarihlerini seçin.");
      setTimeout(() => setErrorMessage(""), 5000); // 5 saniye sonra hata mesajını temizle
      return;
    }

    // Çıkış tarihinin giriş tarihinden sonra olup olmadığını kontrol et
    if (new Date(checkOutDate) <= new Date(checkInDate)) {
      setErrorMessage("Çıkış tarihi, giriş tarihinden sonra olmalıdır.");
      setTimeout(() => setErrorMessage(""), 5000);
      return;
    }

    // Yetişkin ve çocuk sayısının geçerliliği
    if (isNaN(numAdults) || numAdults < 1 || isNaN(numChildren) || numChildren < 0) {
      setErrorMessage("Lütfen yetişkinler ve çocuklar için geçerli sayılar girin.");
      setTimeout(() => setErrorMessage(""), 5000);
      return;
    }

    // Gün sayısı hesaplama
    const oneDay = 24 * 60 * 60 * 1000; // 1 günün milisaniye cinsinden değeri
    const startDate = new Date(checkInDate);
    const endDate = new Date(checkOutDate);
    const totalDays = Math.round(Math.abs((endDate - startDate) / oneDay)) + 1;

    // Toplam misafir sayısı
    const totalGuests = numAdults + numChildren;

    // Toplam fiyat hesaplama
    const roomPricePerNight = room.roomPrice;
    const totalPrice = roomPricePerNight * totalDays;

    setTotalPrice(totalPrice);
    setTotalGuests(totalGuests);
  };

  return (
    <div className="room-details-page">
      <h1>{room.roomName}</h1>
      <p>{room.roomDescription}</p>
      <p>Fiyat: {room.roomPrice} TL / gece</p>

      <div className="date-picker-container">
        <DatePicker
          className="detail-search-field"
          selected={checkInDate}
          onChange={(date) => setCheckInDate(date)}
          selectsStart
          startDate={checkInDate}
          endDate={checkOutDate}
          placeholderText="Giriş Tarihi"
          dateFormat="dd/MM/yyyy"
        />
        <DatePicker
          className="detail-search-field"
          selected={checkOutDate}
          onChange={(date) => setCheckOutDate(date)}
          selectsEnd
          startDate={checkInDate}
          endDate={checkOutDate}
          minDate={checkInDate}
          placeholderText="Çıkış Tarihi"
          dateFormat="dd/MM/yyyy"
        />
        {errorMessage && <p className="error-message">{errorMessage}</p>}
      </div>

      <div className="guest-input-container">
        <label>
          Yetişkin Sayısı:
          <input
            type="number"
            min="1"
            value={numAdults}
            onChange={(e) => setNumAdults(Number(e.target.value))}
          />
        </label>
        <label>
          Çocuk Sayısı:
          <input
            type="number"
            min="0"
            value={numChildren}
            onChange={(e) => setNumChildren(Number(e.target.value))}
          />
        </label>
      </div>

      <button className="confirm-booking-button" onClick={handleConfirmBooking}>
        Rezervasyonu Onayla
      </button>

      {totalPrice > 0 && (
        <div className="booking-summary">
          <h2>Rezervasyon Özeti</h2>
          <p>Toplam Gün Sayısı: {totalPrice / room.roomPrice}</p>
          <p>Toplam Kişi Sayısı: {totalGuests}</p>
          <p>Toplam Fiyat: {totalPrice} TL</p>
        </div>
      )}
    </div>
  );
};

export default RoomDetailsPage;
